<template>
<view class="tip-view">
	<view class="i"></view>
	<text>THIS PAGE MAY BE ON MARS.</text>
	<view class="font">That will be return after at <text>{{count}}</text>s</view>
</view>
</template>

<script>
export default {
	name:'error',
	data(){
		return {
			timer: null,
			count: 5
		}
	},
	mounted(){
		this.timer = setInterval(() => {
			if(this.count <= 0){
				clearInterval(this.timer)
				this.$router.go(-1)
				return
			}
			this.count--
		}, 1000);
	},
	beforeDestroy(){
		clearInterval(this.timer)
	}
}
</script>

<style scoped>
html, body{height:100%; margin:0; padding:0; position:relative; text-align:center; font-family:Arial,sans-serif; -webkit-font-smoothing:antialiased;}
.tip-view{position:absolute; left:0; top:50%; width:100%; height:auto; margin-top:-0.5rem; -webkit-transform:translateY(-50%); transform:translateY(-50%);}
.tip-view .i{display:block; margin:0 auto; width:1.5rem; height:1.5rem; background:no-repeat center center; background-image:url("data:image/png;base64,"); background-size:cover;}
.tip-view > text{display:block; width:100%; height:0.34rem; line-height:0.34rem; font-size:0.18rem;}
.tip-view .font{display:block; width:100%; height:0.2rem; line-height:0.2rem; font-size:0.14rem; color:#ccc;}
.tip-view .font text{font-weight:normal;}
</style>